jQuery(function() {

	/*Start ÉTAT GENERAL Validation*/

	jQuery( '#inchange' ).on( 'change', function() {

		if(getValue('inchange.value') == '162679') {
			setValue('pameliore.value', '');
			setValue('agrave.value', '');
			setValue('nameliore.value', '');
		}
	});

	jQuery( '#pameliore' ).on( 'change', function() {

		if(getValue('pameliore.value') == '1065') {
			setValue('inchange.value', '');
			setValue('agrave.value', '');
			setValue('nameliore.value', '');
		}
	});

	jQuery( '#agrave' ).on( 'change', function() {

		if(getValue('agrave.value') == '162678') {
			setValue('pameliore.value', '');
			setValue('inchange.value', '');
			setValue('nameliore.value', '');
		}
	});

	jQuery( '#nameliore' ).on( 'change', function() {

		if(getValue('nameliore.value') == '162677') {
			setValue('inchange.value', '');
			setValue('agrave.value', '');
			setValue('pameliore.value', '');
		}
	});

	/*End ÉTAT GENERAL Validation*/

});
